const galleryButtonFilters=document.querySelectorAll(".gallery-button-filters")
galleryButtonFilters.forEach(filter=>{filter.addEventListener("click",function(event){event.preventDefault();galleryButtonFilters.forEach(btn=>btn.classList.remove("active-filters"))
filter.classList.add("active-filters");const idFilter=event.target.id
const allGaleryImages=document.querySelectorAll(".gallery-grid img")
allGaleryImages.forEach(img=>{img.closest('a').style.display='none'
img.setAttribute('aria-hidden','true');const categoryId=img.getAttribute("categoryId")
if(idFilter===categoryId){img.closest('a').style.display=null
img.removeAttribute('aria-hidden')}else if(idFilter==="0"){img.closest('a').style.display=null
img.removeAttribute('aria-hidden')}})})})